PREFIX prop: <http://data.lod2.eu/scoreboard/properties/>
PREFIX qb: <http://purl.org/linked-data/cube#>
PREFIX country: <http://data.lod2.eu/scoreboard/country/>

SELECT ?country_name, ?value WHERE {

?observation
    a qb:Observation ;
    prop:indicator $indicator ;
    prop:year $year ;
    prop:country ?country ;
    prop:value ?value .

?country
    rdfs:label ?country_name .

}

LIMIT 1000

PREFIX prop: <http://data.lod2.eu/scoreboard/properties/>
PREFIX qb: <http://purl.org/linked-data/cube#>
PREFIX country: <http://data.lod2.eu/scoreboard/country/>

SELECT (xsd:int(?year_label)) AS ?year, ?value WHERE {

?observation
    a qb:Observation ;
    prop:indicator $indicator ;
    prop:year ?year ;
    prop:country $country ;
    prop:value ?value .

?year
    rdfs:label ?year_label .

}

LIMIT 1000

PREFIX prop: <http://data.lod2.eu/scoreboard/properties/>
PREFIX qb: <http://purl.org/linked-data/cube#>
PREFIX country: <http://data.lod2.eu/scoreboard/country/>

SELECT ?country_name, ?value_x, ?value_y WHERE {

?observation_x
    a qb:Observation ;
    prop:indicator $indicator_x ;
    prop:year $year ;
    prop:country ?country ;
    prop:value ?value_x .

?observation_y
    a qb:Observation ;
    prop:indicator $indicator_y ;
    prop:year $year ;
    prop:country ?country ;
    prop:value ?value_y .

?country
    rdfs:label ?country_name .

}

LIMIT 1000

PREFIX cls: <http://data.lod2.eu/scoreboard/classes/>
PREFIX sbprop: <http://data.lod2.eu/scoreboard/properties/>
PREFIX dcterms: <http://purl.org/dc/terms/>

SELECT * WHERE {

?indicator
    a cls:Indicator ;
    rdfs:label ?label ;
    rdfs:comment ?comment ;
    dcterms:publisher ?publisher ;
    sbprop:unit ?unit .

}

LIMIT 1000

PREFIX prop: <http://data.lod2.eu/scoreboard/properties/>
PREFIX qb: <http://purl.org/linked-data/cube#>
PREFIX country: <http://data.lod2.eu/scoreboard/country/>

SELECT DISTINCT ?indicator, ?year_label WHERE {

?observation
    a qb:Observation ;
    prop:indicator ?indicator ;
    prop:year ?year .

?year
    rdfs:label ?year_label .

}

LIMIT 1000

PREFIX geo: <http://ns.aksw.org/spatialHierarchy/>

SELECT ?country ?label WHERE {

?country
    a ?type ;
    rdfs:label ?label .

FILTER (?type = <http://ns.aksw.org/spatialHierarchy/Country> or
        ?type = <http://ns.aksw.org/spatialHierarchy/SpatialArea>)

}

ORDER BY ?label

LIMIT 1000
